import { React, useState } from "react";
import styles from "./AddUser.module.css";
import Button from "../UI/Button";
import Card from "../UI/Card";

const AddUser = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");



  const nameChangeHandler = (e) => {
    setEnteredName(e.target.value);
  };

  const ageChangeHandler = (e) => {
    setEnteredAge(e.target.value);
  };

  const addUserHandler = (e) => {
    e.preventDefault();
    const newUser = {
      name: enteredName,
      age: enteredAge,
    };
    props.onAddUser((prevUsers) => {
      return [newUser, ...prevUsers];
    });
   setEnteredName("");
   setEnteredAge("");
   e.target.reset();
   e.target[0].focus();
  };

  return (
    <Card>
      <form className={styles.input} onSubmit={addUserHandler}>
        <label htmlFor="username" className={styles.label}>
          Username
        </label>
        <input id="username" type="text" value="Add user name" onChange={nameChangeHandler}></input>
        <label htmlFor="age" className={styles.label}>
          Age (years)
        </label>
        <input id="age" type="number" min={1} value="Add user age" onChange={ageChangeHandler}></input>
        <Button />
      </form>
    </Card>
  );
};

export default AddUser;
