import { React, useState } from "react";
import styles from "./AddUser.module.css";
import Button from "../UI/Button";
import Card from "../UI/Card";

const AddUser = (props) => {

    const [users, addNewUser] = useState('')
  const addUserHandler = (e) => {
    e.preventDefault();
    const newUser = {
        name: e.target[0].value,
        age: e.target[1].value
    }
    addNewUser((prevUsers) => {
        return [newUser, ...prevUsers];
      });
    /* console.log(e.target[0].value);
    console.log(e.target[1].value); */
  };

  return (
    <Card>
      <form className={styles.input} onSubmit={addUserHandler}>
        <label htmlFor="username" className={styles.label}>
          Username
        </label>
        <input id="username" type="text" className={styles.input}></input>
        <label htmlFor="age" className={styles.label}>
          Age (years)
        </label>
        <input id="age" type="number" className={styles.input}></input>
        <Button />
      </form>
    </Card>
  );
};

export default AddUser;
